import React, { useState } from 'react';




const App = () =>{ 
  let newTime = new Date().toLocaleTimeString() ;
  const [oldTime,curTime] = useState(newTime) ;
  
  const updateTime = ()=>{
    curTime(new Date().toLocaleTimeString()) ;
  } 
  
  setInterval(updateTime,1000) ;
  return (
    <>
      <h1>
      {newTime}</h1>
    </>
  );
  
};

export default App ;
