import React, { useState } from 'react';





const App = () =>{

  const BgChange = () =>{
    const purple = 'purple' ;
    const [bg,setbg] = useState(purple) ;
    setbg( purple = 'red') ;
  } 
  
  return (
    <>
      <div style = {{ backgroundColor: purple}}>
        <button onClick={BgChange}> Click Me</button>
      </div> 
    </>
  );
  
};

export default App ;
