import React, { useState } from 'react';

const mycolor = 'green' ;
const BgChange = () =>{
  const [mycolor,newColor] = useState('green') ;
  newColor( mycolor = 'red') ;
}



const App = () =>{ 
  
  return (
    <>
      <div style = {{ backgroundColor: mycolor}}>
        <button onClick={BgChange}> Click Me</button>
      </div> 
    </>
  );
  
};

export default App ;
