import React, { useState } from 'react';




const App = () =>{ 
  //const state = useState() ;
  let count = 1 ;
  [count] = useState(count,IncNum) ;
  
  const IncNum = ()=>{
      count++;
  }   
  return (
    <>
      <h1>{count}</h1>
      <button onClick={IncNum}>Click Me</button>
    </>
  );
  
};

export default App ;
